mapper.select:
SELECT m.*, g.*, g.name AS g_name, c.*, c.name AS c_name, (SELECT COUNT(*) FROM apply a WHERE a.id = m.id and a.status = '승인 완료') AS apply_count FROM members m LEFT JOIN grade g ON m.grade_seq = g.seq LEFT JOIN company c ON c.member_id = m.id WHERE m.role = 'ROLE_BIZ'
AND (c.name LIKE '%'|| #{keyword}|| '%' OR m.name LIKE '%'|| #{keyword} || '%')
AND m.grade_seq = #{gradeSeq}
